updatefound(/* registration */) {
  const installKey = 'chatAppInstalled';
  if (localStorage.getItem(installKey)) {
    Notify.create({
      color: 'dark',
      message: 'An update is being downloaded from the server.',
    });
  } else {
    localStorage.setItem(installKey, '1');
  }
},
