 **},**
 **animalCodes () {**
 **return this.$store.state.animalCodes**
 **},**
 **sadSrc () {**
 **return this.$store.state.sadSrc**
 **},**
 **gladSrc () {**
 **return this.$store.state.gladSrc**
 **}**
 **},**
    methods: {
      onNameChanged (newName) {
        this.name = newName
      }
    }
  }
</script>
